styles:
html {
  font-size: 0.3cm;
}

@media screen {
  html {
    width: 21cm;
  }
  body {
    border: 1px dotted black;
  }
}
.report-container {
  font-size: 9pt !important;
}
@media screen {
  .report-container {
    width: 21cm;
  }
}

@media print {
  html {
    width: 100%;
  }
}
body {
  margin: 0;
  padding: 0.3cm;
}

.page {
  width: 100%;
}

.frame {
  border-bottom: 1px solid black;
  border-right: 1px solid black;
}

.band.h {
  display: grid;
  grid-auto-flow: column;
  grid-auto-columns: 1fr;
  flex-direction: row;
}

.band.v {
  display: flex;
  flex-direction: column;
}

.slot {
  flex: 1;
  padding: 0.1rem 0.2rem;
  min-height: 2rem;
  border-top: 1px solid black;
  border-left: 1px solid black;
}

hr {
  border: 0;
}

hr.dashed {
  border: 0;
  border-top: 1px dashed black;
}

label {
  display: block;
}

label.header {
  margin-top: 0.4rem;
  margin-bottom: 0.1rem;
}

th {
  text-align: unset;
}

label,
th {
  font-size: 60%;
  font-weight: bold;
  text-transform: uppercase;
}

.center {
  text-align: center;
}

.number {
  text-align: right;
}

img {
  padding: 0 5%;
  width: 90%;
  height: 100%;
  object-fit: contain;
}

h1 {
  font-size: 100%;
}

table {
  border-collapse: collapse;
}

th,
td {
  border: 1px solid black;
  padding: 0.1rem 0.2rem;
}
@font-face {
  font-family: "AnekLatin";
  src: url("https://s3.sa-east-1.amazonaws.com/zenerp.app.br/assets/fonts/AnekLatin.ttf") format("truetype");
  font-weight: normal;
  font-style: normal;
}
html,
body {
  margin: 0;
  padding: 0;
}

.report-wrapper {
  --border-radius: 0.2em;
  --color-border: rgba(0, 0, 0, 0.1);
  --color-shadow: rgba(0, 0, 0, 0.1);
  --gap: 0.5rem;
  --padding: 0.35rem;
  font-family: "AnekLatin", "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  font-size: 11pt;
  background-color: var(--color-shadow);
}
@media screen {
  .report-wrapper {
    padding: 1rem;
  }
}
.report-wrapper .report-container {
  position: relative;
  page-break-after: always;
  break-after: page;
  background-color: white;
}
.report-wrapper .report-container .stamp {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) rotate(-45deg);
  font-size: 300%;
  opacity: 0.3;
  color: red;
}
.report-wrapper .report-container > header {
  display: flex;
  flex-direction: column;
  gap: var(--gap);
  border-bottom: 3px solid #000;
  padding-bottom: 1rem;
  margin-bottom: 1rem;
}
.report-wrapper .report-container > header .brand {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
}
.report-wrapper .report-container > header h1 {
  margin: 0;
  font-size: 20pt;
}
.report-wrapper .report-container > header dl {
  display: flex;
  gap: var(--gap);
  margin: 0;
}
.report-wrapper .report-container > header dl div {
  flex: 1;
  border: 1px solid var(--color-border);
  border-radius: var(--border-radius);
  padding: var(--padding);
}
.report-wrapper .report-container > header dl div dt {
  font-size: 8pt;
  color: gray;
  text-transform: uppercase;
  font-weight: bold;
}
.report-wrapper .report-container > header dl div dd {
  margin: 0;
}
.report-wrapper .report-container > main .kpi-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1rem;
  margin-bottom: 2rem;
}
.report-wrapper .report-container > main .kpi-grid dl {
  border: 1px solid var(--color-border);
  padding: 1rem;
  background: #fdfdfd;
  display: flex;
  justify-content: space-between;
}
.report-wrapper .report-container > main .kpi-grid dl dt {
  font-size: 9pt;
  color: gray;
}
.report-wrapper .report-container > main .kpi-grid dl dd {
  margin: 0;
  font-size: 16pt;
  font-weight: bold;
}
.report-wrapper .report-container > main .kpi-grid dl .kpi-trend {
  font-size: 8pt;
  align-self: flex-end;
}
.report-wrapper .report-container > main section {
  margin-top: 1.5rem;
}
.report-wrapper .report-container > main section section {
  padding-left: 1rem;
}
.report-wrapper .report-container > main section > header {
  font-weight: bold;
  border-bottom: 1px solid var(--color-border);
  margin-bottom: 0.5rem;
}
.report-wrapper .report-container > main section .content table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 0.5rem;
}
.report-wrapper .report-container > main section .content table th {
  text-align: left;
  background: #f8f9fa;
  padding: 8px;
  font-size: 9pt;
}
.report-wrapper .report-container > main section .content table td {
  padding: 8px;
  border-bottom: 1px solid #f1f1f1;
}
.report-wrapper .report-container > main section .content table tfoot td {
  font-weight: bold;
  border-top: 2px solid #333;
}
.report-wrapper .report-container > main section > footer {
  font-size: 9pt;
  font-style: italic;
  color: gray;
  padding-top: 5px;
}
.report-wrapper .report-container > footer {
  margin-top: 4rem;
  border-top: 2px solid #000;
  padding-top: 1rem;
}
.report-wrapper .report-container > footer .signature-block {
  display: flex;
  justify-content: space-between;
  margin-top: 2rem;
}
.report-wrapper .report-container > footer dl {
  display: flex;
  gap: var(--gap);
  margin: 0;
}
.report-wrapper .report-container > footer dl div {
  flex: 1;
  border: 1px solid var(--color-border);
  border-radius: var(--border-radius);
  padding: var(--padding);
}
.report-wrapper .report-container > footer dl div dt {
  font-size: 8pt;
  color: gray;
  text-transform: uppercase;
  font-weight: bold;
}
.report-wrapper .report-container > footer dl div dd {
  margin: 0;
}
.report-wrapper .flex {
  display: flex;
}
.report-wrapper .flex.h {
  flex-direction: row;
}
.report-wrapper .flex.v {
  flex-direction: column;
}
.report-wrapper .flex.align-center {
  align-items: center;
}
.report-wrapper .flex.justify-center {
  justify-content: space-between;
}
.report-wrapper .flex.gap {
  gap: 0.5rem;
}
.report-wrapper .flex .flex-1 {
  flex: 1;
}
.report-wrapper .text-right,
.report-wrapper .number {
  text-align: right !important;
}
.report-wrapper .number {
  font-variant-numeric: tabular-nums;
}
.report-wrapper .text-success {
  color: green;
}
.report-wrapper .text-danger {
  color: red;
}
.report-wrapper pre {
  font-family: "AnekLatin", "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  white-space: pre-wrap;
  word-wrap: break-word;
  overflow-wrap: break-word;
  margin: 0;
}
